import { ADD_FILTER, CLEAR_FILTER, REMOVE_FILTER } from "./filter-action";

export const filterReducer = (state = [], action) => {
    switch (action.type) {
        case ADD_FILTER: {
            return [...state, action.filter];
        }
        case REMOVE_FILTER: {
            return state.filter(item => item !== action.filter)
        }
        case CLEAR_FILTER: {
            return [];
        }
        default: {
            return state;
        }
    }
}
